INSERT INTO `servicos` (`id_servicos`, `texto`) VALUES
(1, '<p><strong>CONDOM&Iacute;NIO ONLINE</strong></p>\r\n<p>&nbsp;</p>\r\n<p># Emiss&atilde;o de segunda via de boleto<br /> # Presta&ccedil;&otilde;es de Contas<br /> # Balancete Mensal<br /> # Posi&ccedil;&atilde;o de inadimplente<br /> # Disponibiliza&ccedil;&atilde;o de Atas de Assembl&eacute;ia<br /> # Disponibiliza&ccedil;&atilde;o da Conven&ccedil;&atilde;o e Regimento Interno<br /> # Editais de Convoca&ccedil;&otilde;es para Assembl&eacute;ias<br /> # Avisos e Informativos<br /> # As reserva de &Aacute;reas comuns e agendamento de mudan&ccedil;a s&atilde;o feitos atrav&eacute;s da internet na &aacute;rea do cliente.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>OPERACIONAL</strong></p>\r\n<p>&nbsp;</p>\r\n<p># Acompanhamentos e Fiscaliza&ccedil;&atilde;o dos prestadores de servi&ccedil;os contratados pelo condom&iacute;nio<br /> # Vistorias peri&oacute;dicas na unidade, para verifica&ccedil;&atilde;o do livro de ocorr&ecirc;ncia, bem como fiscaliza&ccedil;&atilde;o dos funcion&aacute;rios e servi&ccedil;os.<br /> # Estudo e viabilidade para reduzir custo do condom&iacute;nio<br /> # Mediador nos conflitos internos e nas cobran&ccedil;as de inadimplentes.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><strong>CONT&Aacute;BIL E FINANCEIRO</strong></p>\r\n<p>&nbsp;</p>\r\n<p># Contabiliza&ccedil;&atilde;o do movimento mensal (receitas e despesas)<br /> # Apresenta&ccedil;&atilde;o de balancetes mensais (on-line)<br /> # Concilia&ccedil;&atilde;o banc&aacute;ria<br /> # Relat&oacute;rios de inadimplentes, de contas a pagar, de contas a receber.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>TESOURARIA</strong></p>\r\n<p>&nbsp;</p>\r\n<p># Servi&ccedil;os banc&aacute;rios<br /> # Pagamento de funcion&aacute;rios<br /> # Pagamento de fornecedores e prestadores de servi&ccedil;o<br /> # Pagamentos de taxas, tributos e impostos do condom&iacute;nio.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>RECURSOS HUMANOS</strong></p>\r\n<p>&nbsp;</p>\r\n<p># Recrutamento, sele&ccedil;&atilde;o e administra&ccedil;&atilde;o de pessoal.<br /> # Elabora&ccedil;&atilde;o da folha de pagamento e das respectivas guias das obriga&ccedil;&otilde;es sociais<br /> # Rela&ccedil;&otilde;es sindicais<br /> # Treinamentos</p>\r\n<p>&nbsp;</p>\r\n<p><strong>JUR&Iacute;DICO</strong></p>\r\n<p>&nbsp;</p>\r\n<p># Atualiza&ccedil;&atilde;o permanente da legisla&ccedil;&atilde;o que envolve a administra&ccedil;&atilde;o de Condom&iacute;nios.<br /> # Consultoria tribut&aacute;ria, fiscal, civil e trabalhista.<br /> # Assessoria, quando solicitado, na elabora&ccedil;&atilde;o e altera&ccedil;&atilde;o da Conven&ccedil;&atilde;o de Condom&iacute;nio.<br /> # Assessoria, quando solicitado, na assinatura de contratos de presta&ccedil;&atilde;o de servi&ccedil;os, de terceiriza&ccedil;&atilde;o etc.<br /> # Desenvolvimento na cobran&ccedil;a extrajudicial dos  inadimplentes<br /> # Acompanhamento na cobran&ccedil;a judicial dos devedores de cotas<br /> # Acompanhamento e Emiss&atilde;o de relat&oacute;rios peri&oacute;dicos abrangendo o andamento das a&ccedil;&otilde;es Judiciais.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>OUTROS SERVI&Ccedil;OS</strong></p>\r\n<p>&nbsp;</p>\r\n<p># Retirada de certid&otilde;es nos cart&oacute;rios<br /> # Altera&ccedil;&otilde;es na conven&ccedil;&atilde;o do condom&iacute;nio e/ou regulamento interno<br /> # Negocia&ccedil;&atilde;o de acordos junto a Cosern, Caern, INSS, FGTS<br /> # Regulariza&ccedil;&atilde;o de processos junto ao INSS e Secretaria da Fazenda (CNPJ)<br /> # Individualiza&ccedil;&atilde;o de &aacute;gua e G&aacute;s<br /> # Registro de incorpora&ccedil;&atilde;o<br /> # Mudan&ccedil;a de Titularidade nas concession&aacute;rias p&uacute;blicas e privadas</p>\r\n<p>&nbsp;</p>');
